#!/bin/bash

usage() {
    echo \
'Run "PRIVATE_KEY=0x1234 ./script/config_dcap_sgx_verifier.sh 
    --tcb file_path: config tcb
    --eq file_path: config qe
    --mrenclave hex_string: config mrenclave
    --mrsigner hex_string: config mrsigner
    --quote string: register sgx instance with quote"
to configure the dcap verifier contract.

Typical usages:
1. For admin to init config all, run:
    PRIVATE_KEY=admin_privkey ./script/config_dcap_sgx_verifier.sh --tcb /test/automata-attestation/assets/0923/tcbInfo_00606A000000.json --qeid /test/automata-attestation/assets/0923/identity.json --mrenclave 0xdeadbeefdeadbeefdeadbeefdeadbeefdeadbeefdeadbeefdeadbeefdeadbeef --mrsigner 0xdeadbeefdeadbeefdeadbeefdeadbeefdeadbeefdeadbeefdeadbeefdeadbeef

2. For admin to config tcb, run:
    PRIVATE_KEY=0x1234 ./script/config_dcap_sgx_verifier.sh --tcb file_path

3. For user to register sgx instance with quote, run:
    PRIVATE_KEY=0x1234 ./script/config_dcap_sgx_verifier.sh --quote string'
}

if [ $# -eq 0 ]; then
    usage
    exit 1
fi

# replace with the correct address of the verifier/attester/pemCertChain.
export SGX_VERIFIER_ADDRESS=0x532EFBf6D62720D0B2a2Bb9d11066E8588cAE6D9
export ATTESTATION_ADDRESS=0xC6cD3878Fc56F2b2BaB0769C580fc230A95e1398
export PEM_CERTCHAIN_ADDRESS=0x08d7865e7F534d743Aba5874A9AD04bcB223a92E

# default value
# for setMrEnclave which should be called by the owner of the verifier
MR_ENCLAVE=0xdeadbeefdeadbeefdeadbeefdeadbeefdeadbeefdeadbeefdeadbeefdeadbeef
# for setMrSigner which should be called by the owner of the verifier
MR_SIGNER=0xdeadbeefdeadbeefdeadbeefdeadbeefdeadbeefdeadbeefdeadbeefdeadbeef
# for configQE which should be called by the owner of the verifier
QEID_PATH=/test/automata-attestation/assets/0923/identity.json
# for configTCB which should be called by the owner of the verifier
TCB_INFO_PATH=/test/automata-attestation/assets/0923/tcbInfo_00606A000000.json
# for registerSgxInstanceWithQuote which is a public function
V3_QUOTE_

# test flag
config_tcb=0
config_qe=0
set_mrenclave=0
set_mrsigner=0
verify_quote=0

# helper function for trimming the file path to vm root
vm_file_path() {
    local file_path="$1"
    local parent_dir=$(dirname "$file_path")
    local trimmed_path="/test/automata-attestation/assets/$(basename "$parent_dir")/$(basename "$file_path")"
    echo "$trimmed_path"
}

# cmdline args
while [[ $# -gt 0 ]]; do
    key="$1"
    case $key in
        --mrenclave)
            MR_ENCLAVE="$2"
            echo "Set MR_ENCLAVE: $MR_ENCLAVE"
            set_mrenclave=1
            shift
            shift
            ;;
        --mrsigner)
            MR_SIGNER="$2"
            echo "Set MR_SIGNER: $MR_SIGNER"
            set_mrsigner=1
            shift
            shift
            ;;
        --qeid)
            QEID_PATH=$(vm_file_path "$2")
            echo "Config QE file: $QEID_PATH"
            config_qe=1
            shift
            shift
            ;;
        --tcb)
            TCB_INFO_PATH=$(vm_file_path "$2")
            echo "Config TCB file: $TCB_INFO_PATH"
            config_tcb=1
            shift
            shift
            ;;
        --quote)
            V3_QUOTE_BYTES=$2
            echo "Register SGX Quote."
            verify_quote=1
            shift
            shift
            ;;
        *)
            echo "Unknown option: $1"
            exit 1
            ;;
    esac
done

if [ -z $FORK_URL ]; then
    FORK_URL="http://localhost:8545/" # any-eth-node-rpc-url
fi

# TASK_FLAG: [setMrEnclave,setMrSigner,configQE,configTCB,registerSgxInstanceWithQuote]
TASK_ENABLE_MASK="$set_mrenclave,$set_mrsigner,$config_qe,$config_tcb,$verify_quote"

# config the contract
TASK_ENABLE=$TASK_ENABLE_MASK \
MR_ENCLAVE=$MR_ENCLAVE \
MR_SIGNER=$MR_SIGNER \
QEID_PATH=$QEID_PATH \
TCB_INFO_PATH=$TCB_INFO_PATH \
V3_QUOTE_BYTES=$V3_QUOTE_BYTES \
forge script script/SetDcapParams.s.sol:SetDcapParams \
    --fork-url $FORK_URL \
    --broadcast \
    --ffi \
    -vvvv \
    --block-gas-limit 100000000 \
    --private-key ${PRIVATE_KEY}
