#!/bin/bash

usage() {
    echo \
        'Run "PRIVATE_KEY=0x1234 ./script/config_dcap_sgx_verifier.sh
    --env [dev-<ontake|pacaya|pivot>|hekla-<ontake|pacaya|pivot>|mainnet], default is dev-ontake
    --tcb file_path: config tcb
    --eq file_path: config qe
    --mrenclave hex_string: config mrenclave
    --mrsigner hex_string: config mrsigner
    --toggle-mr-check: toggle mrenclave/mrsigner check
    --unset-mrenclave hex_string: disable mrenclave
    --unset-mrsigner hex_string: disable mrsigner
    --quote string: register sgx instance with quote"
to configure the dcap verifier contract.

Typical usages:
1. For admin to init config all, run:
    PRIVATE_KEY=admin_privkey ./script/layer1/provers/config_dcap_sgx_verifier.sh --tcb /test/layer1/automata-attestation/assets/0923/tcbInfo_00606A000000.json --qeid /test/layer1/automata-attestation/assets/0923/identity.json --mrenclave 0xdeadbeefdeadbeefdeadbeefdeadbeefdeadbeefdeadbeefdeadbeefdeadbeef --mrsigner 0xdeadbeefdeadbeefdeadbeefdeadbeefdeadbeefdeadbeefdeadbeefdeadbeef

2. For admin to config tcb, run:
    PRIVATE_KEY=0x1234 ./script/layer1/provers/config_dcap_sgx_verifier.sh --tcb file_path

3. For user to register sgx instance with quote, run:
    PRIVATE_KEY=0x1234 ./script/layer1/provers/config_dcap_sgx_verifier.sh --quote string'
}

if [ $# -eq 0 ]; then
    usage
    exit 1
fi

#default env: dev-ontake
export SGX_VERIFIER_ADDRESS=0x6f6E456354A33BDe7B0ED4A10759b79AC0192e68
export ATTESTATION_ADDRESS=0xACFFB14Ca4b783fe7314855fBC38c50d7b7A8240
export PEM_CERTCHAIN_ADDRESS=0xF3152569f2f74ec0f3fd0f57C09aCe07adDA7c5D

load_env() {
    local env="$1"
    case "$env" in
    dev-ontake)
        export SGX_VERIFIER_ADDRESS=0x6f6E456354A33BDe7B0ED4A10759b79AC0192e68
        export ATTESTATION_ADDRESS=0xACFFB14Ca4b783fe7314855fBC38c50d7b7A8240
        export PEM_CERTCHAIN_ADDRESS=0xF3152569f2f74ec0f3fd0f57C09aCe07adDA7c5D
        ;;
    dev-pacaya)
        export SGX_VERIFIER_ADDRESS=0x6f6E456354A33BDe7B0ED4A10759b79AC0192e68
        export ATTESTATION_ADDRESS=0xACFFB14Ca4b783fe7314855fBC38c50d7b7A8240
        export PEM_CERTCHAIN_ADDRESS=0xF3152569f2f74ec0f3fd0f57C09aCe07adDA7c5D
        ;;
    dev-pivot)
        export SGX_VERIFIER_ADDRESS=0x9016078d9870e432cb47122609F83DeE025bC060
        export ATTESTATION_ADDRESS=0x0d272C6C9099330A0229fE0954973506416277e9
        export PEM_CERTCHAIN_ADDRESS=0xF3152569f2f74ec0f3fd0f57C09aCe07adDA7c5D
        ;;
    hekla-ontake)
        export SGX_VERIFIER_ADDRESS=0x532EFBf6D62720D0B2a2Bb9d11066E8588cAE6D9
        export ATTESTATION_ADDRESS=0xC6cD3878Fc56F2b2BaB0769C580fc230A95e1398
        export PEM_CERTCHAIN_ADDRESS=0x08d7865e7F534d743Aba5874A9AD04bcB223a92E
        ;;
    hekla-pacaya)
        export SGX_VERIFIER_ADDRESS=0xa8cD459E3588D6edE42177193284d40332c3bcd4
        export ATTESTATION_ADDRESS=0xC6cD3878Fc56F2b2BaB0769C580fc230A95e1398
        export PEM_CERTCHAIN_ADDRESS=0x08d7865e7F534d743Aba5874A9AD04bcB223a92E
        ;;
    mainnet)
        export SGX_VERIFIER_ADDRESS=0xb0f3186FC1963f774f52ff455DC86aEdD0b31F81
        export ATTESTATION_ADDRESS=0x8d7C954960a36a7596d7eA4945dDf891967ca8A3
        export PEM_CERTCHAIN_ADDRESS=0x02772b7B3a5Bea0141C993Dbb8D0733C19F46169
        ;;
    *)
        echo "‚ùå Unknown environment: $env"
        echo "Usage: load_env [dev|hekla-ontake|hekla-pacaya|mainnet]"
        return 1
        ;;
    esac

    echo "Loaded environment: $env"
    echo "SGX_VERIFIER_ADDRESS: $SGX_VERIFIER_ADDRESS"
    echo "ATTESTATION_ADDRESS: $ATTESTATION_ADDRESS"
    echo "PEM_CERTCHAIN_ADDRESS: $PEM_CERTCHAIN_ADDRESS"
}

# default value
# for setMrEnclave which should be called by the owner of the verifier
MR_ENCLAVE=0xdeadbeefdeadbeefdeadbeefdeadbeefdeadbeefdeadbeefdeadbeefdeadbeef
# for setMrSigner which should be called by the owner of the verifier
MR_SIGNER=0xdeadbeefdeadbeefdeadbeefdeadbeefdeadbeefdeadbeefdeadbeefdeadbeef
# for configQE which should be called by the owner of the verifier
QEID_PATH=/test/automata-attestation/assets/0923/identity.json
# for configTCB which should be called by the owner of the verifier
TCB_INFO_PATH=/test/automata-attestation/assets/0923/tcbInfo_00606A000000.json
# for registerSgxInstanceWithQuote which is a public function
V3_QUOTE_

# test flag
config_tcb=0
config_qe=0
set_mrenclave=0
set_mrsigner=0
verify_quote=0
toggle_check=0

# helper function for trimming the file path to vm root
vm_file_path() {
    local file_path="$1"
    local parent_dir=$(dirname "$file_path")
    local trimmed_path="/test/layer1/automata-attestation/assets/$(basename "$parent_dir")/$(basename "$file_path")"
    echo "$trimmed_path"
}

# cmdline args
while [[ $# -gt 0 ]]; do
    key="$1"
    case $key in
    --mrenclave)
        MR_ENCLAVE="$2"
        echo "Set MR_ENCLAVE: $MR_ENCLAVE"
        set_mrenclave=1
        shift
        shift
        ;;
    --mrsigner)
        MR_SIGNER="$2"
        echo "Set MR_SIGNER: $MR_SIGNER"
        set_mrsigner=1
        shift
        shift
        ;;
    --unset-mrenclave)
        MR_ENCLAVE="$2"
        echo "Unset MR_ENCLAVE: $MR_ENCLAVE"
        set_mrenclave=2
        shift
        shift
        ;;
    --unset-mrsigner)
        MR_SIGNER="$2"
        echo "Unset MR_SIGNER: $MR_SIGNER"
        set_mrsigner=2
        shift
        shift
        ;;
    --toggle-mr-check)
        echo "toggle mr check"
        toggle_check=1
        shift
        shift
        ;;
    --qeid)
        QEID_PATH=$(vm_file_path "$2")
        echo "Config QE file: $QEID_PATH"
        config_qe=1
        shift
        shift
        ;;
    --tcb)
        TCB_INFO_PATH=$(vm_file_path "$2")
        echo "Config TCB file: $TCB_INFO_PATH"
        config_tcb=1
        shift
        shift
        ;;
    --quote)
        V3_QUOTE_BYTES=$2
        echo "Register SGX Quote."
        verify_quote=1
        shift
        shift
        ;;
    --env)
        config_env="$2"
        echo "Running env $config_env."
        load_env $config_env
        shift
        shift
        ;;
    *)
        echo "Unknown option: $1"
        exit 1
        ;;
    esac
done

if [ -z $FORK_URL ]; then
    FORK_URL="http://localhost:8545/" # any-eth-node-rpc-url
fi

# TASK_FLAG: [setMrEnclave,setMrSigner,configQE,configTCB,registerSgxInstanceWithQuote]
TASK_ENABLE_MASK=$set_mrenclave,$set_mrsigner,$config_qe,$config_tcb,$toggle_check,$verify_quote

# config the contract
TASK_ENABLE=$TASK_ENABLE_MASK \
    MR_ENCLAVE=$MR_ENCLAVE \
    MR_SIGNER=$MR_SIGNER \
    QEID_PATH=$QEID_PATH \
    TCB_INFO_PATH=$TCB_INFO_PATH \
    V3_QUOTE_BYTES=$V3_QUOTE_BYTES \
    forge script script/layer1/provers/SetDcapParams.s.sol:SetDcapParams \
    --fork-url $FORK_URL \
    --broadcast \
    --evm-version cancun \
    --ffi \
    -vvvv \
    --block-gas-limit 100000000 \
    --private-key ${PRIVATE_KEY} \
    --legacy
